cue = {}
function cue.new() local self = {}
function self:push(data) self[#self+1]=data end
function self:getTruthTable() local tab = {}
for i=0,77 do tab[i]=false end for i=1,#self do tab[self[i]]=true end return tab end
function self:pop(index) index = index or 1 if #self >= index then local data = self[index] table.remove(self, index) return data end end
function self:isEmpty() return #self == 0 end
function self:clear() while not self:isEmpty() do self:pop() end end
return self end

function tarot.main.rndGetCard()
	return tarot.main[math.random(1, #tarot.main)]
end
function tarot.main.rndPopCard()
	return tarot.main.pop(math.random(#tarot.main))
end

fluxus.push('Fonctions sur la main créés')

