tarot.joueur={}
for i=1,4 do
	tarot.joueur[i]={}
	tarot.joueur[i].plis=cue.new()
	tarot.joueur[i].cartes={}
	tarot.joueur[i].cartes.possible={}
	tarot.joueur[i].cartes.probable={}
	for c=0,77 do
		tarot.joueur[i].cartes.possible[i]=true
		tarot.joueur[i].cartes.probable[i]=50
	end
end

